var logo=document.getElementById("logo"),about=document.getElementById("btnAbout"),work=document.getElementById("btnWork"),contact=document.getElementById("btnContact");logo.onclick=function(){TweenLite.to(window,1,{scrollTo:{y:"header",offsetY:300}})},about.onclick=function(){TweenLite.to(window,1,{scrollTo:"#about"})},work.onclick=function(){TweenLite.to(window,1,{scrollTo:"#work"})},contact.onclick=function(){TweenLite.to(window,1,{scrollTo:"#contact"})};var controller=new ScrollMagic.Controller;$(function(){new ScrollMagic.Scene({triggerElement:".name",duration:500}).setClassToggle("#header-line-right","drawing").addTo(controller)}),$(function(){new ScrollMagic.Scene({triggerElement:"#about",duration:300}).setClassToggle("#about-line","drawing").addTo(controller)}),$(function(){new ScrollMagic.Scene({triggerElement:"#trigger",duration:300}).setClassToggle("#work-line","drawing").addTo(controller)}),$(function(){new ScrollMagic.Scene({triggerElement:"#contact",duration:700}).setClassToggle("#contact-line","drawing").addTo(controller)});